import ReactApexChart from "react-apexcharts";
import React, { Component } from "react";
import {
  MDBEdgeHeader,
  MDBFreeBird,
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBCardBody,
  MDBIcon,
  MDBCard,
  MDBCardTitle,
  MDBCardImage,
  MDBCardText,
  MDBAnimation,
  MDBNavLink,
} from "mdbreact";

// Data generation
/*function getRandomNumber() {
    // Create random array of objects
    return Math.round(20 + 80 * Math.random())
    
}*/

class Temperature extends Component {
  constructor(props) {
    super(props);

    this.state = {
      series: [0],
      options: {
        chart: {
          height: 350,
          type: "radialBar",
        },
        plotOptions: {
          radialBar: {
            hollow: {
              size: "50%",
            },
            dataLabels: {
              show: true,
              name: {
                show: true,
                fontSize: "16px",
                fontFamily: undefined,
                fontWeight: 600,
                color: undefined,
                offsetY: -10,
              },
              value: {
                show: true,
                fontSize: "14px",
                fontFamily: undefined,
                fontWeight: 400,
                color: undefined,
                offsetY: 16,
                formatter: function (val) {
                  return val + "℃";
                },
              },
            },
          },
        },
        labels: ["Temperature"],
      },
    };
  }

  componentDidMount() {
    //Set update interval
    window.setInterval(() => {
      fetch("http://www.smartmonitoring.somee.com/api/Data/GetLastData")
        .then((res) => res.json())
        .then((resJson) => {
          this.setState({
            series: [parseInt(Math.round(resJson[0].temperature, 0))],
          });
        })
        .catch((e) => {
          console.log("Error : " + e);
        });

      /*fetch('http://www.smartmonitoring.somee.com/api/Data/GetLastData', { 
          method: 'get', 
          headers: new Headers({
            // Your header content
          })
        }).then((res) => res.json())
        .then((resJson) => {
          console.log("resJson : ", resJson);
          console.log("resJson : ", resJson.temperature);
          this.setState({ series: resJson.temperature });
        })
        .catch((e) => {
          console.log("Error : ", e);
        });*/

      /*fetch("http://www.smartmonitoring.somee.com/api/Data/GetLastData")
          .then((res) => res.json())
          .then((resJson) => {
            console.log("resJson : ", resJson);
            this.setState({ series: resJson.temperature });
          })
          .catch((e) => {
            console.log("Error : ", e);
          });*/

      /*this.setState({
          series: [getRandomNumber()]
        })*/
    }, 3000);
  }

  render() {
    return (
      <>
        <div className="app">
          <div className="row">
            <div id="chart">
              <ReactApexChart
                options={this.state.options}
                series={this.state.series}
                type="radialBar"
                height={350}
              />
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Temperature;
